mapper.insert:
      select nvl(max(step_num),0)+1 from recipe_step
    
	INSERT
	INTO RECIPE_STEP
	VALUES(#{step_num},#{recipe_num}, #{step_index}, #{step_original, jdbcType=VARCHAR}, 
	#{step_db, jdbcType=VARCHAR}, #{step_content}
		)

UPDATE recipe_step
SET STEP_INDEX = #{STEP_INDEX}, STEP_CONTENT= #{STEP_CONTENT}, 
	STEP_ORIGINAL = #{STEP_ORIGINAL}, STEP_DB = #{STEP_DB}
WHERE recipe_num=#{recipe_num}
